ALTER TRIGGER [dbo].[deductions_update] ON [dbo].[BATCH_DEDUCTIONS] AFTER UPDATE AS BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
declare @tramount numeric(12,2);
select @tramount = (new_row.factor * new_row.rate) from inserted new_row
IF (UPDATE(factor) OR UPDATE(rate)) BEGIN update batch_deductions set amount = @tramount from batch_deductions, inserted new_row where batch_deductions.batchno = new_row.batchno and batch_deductions.empid = new_row.empid and batch_deductions.item_no = new_row.item_no and batch_deductions.itemcode = new_row.itemcode END
